mapper.select:
SELECT id, namespace_id, group_name, data_id FROM sys_config WHERE namespace_id = #{namespaceId}
AND group_name LIKE CONCAT('%',#{groupName},'%') AND data_id LIKE CONCAT('%',#{dataId},'%')
AND group_name = #{groupName} AND data_id #{dataId}
ORDER BY last_update_time DESC LIMIT #{page},#{size}

SELECT
    COUNT(*)
FROM
    sys_config
WHERE namespace_id = #{namespaceId}

    AND group_name LIKE CONCAT('%',#{groupName},'%')
    AND data_id LIKE CONCAT('%',#{dataId},'%')


    AND group_name = #{groupName}
    AND data_id #{dataId}

mapper.delete:
DELETE
FROM
    sys_config
WHERE namespace_id = #{namespaceId}
AND group_name = #{groupName}
AND data_id = #{dataId}

mapper.select:
SELECT
    current_version_id
FROM
    sys_config
WHERE namespace_id = #{namespaceId}
AND group_name = #{groupName}
AND data_id = #{dataId}
